mapper.select:
SELECT p.id, p.name, p.category_id, c.name AS category_name, p.description, p.starting_price, p.main_image, p.images, p.status, p.create_time, p.update_time FROM product_product p LEFT JOIN product_category c ON p.category_id = c.id
AND p.name LIKE CONCAT('%', #{dto.name}, '%')
AND p.category_id = #{dto.categoryId}
AND p.status = #{dto.status}
AND p.status IN (0, 1)
order by p.create_time desc
